@import '../../globalData';
@import url(https://fonts.googleapis.com/css2?family=Style+Script&display=swap);
.login-page{
    display: flex;
    align-items: center;
    justify-content: center;
    min-height: calc(100vh - (55px + 80px));
    
    gap: 20px;
    background-color: $primary;
    .login-content-1{
        background-image: url(https://www.instagram.com/static/images/homepage/home-phones@2x.png/9364675fb26a.png);
        background-size: 100%;
        background-repeat: no-repeat;
        width: 400px;
        height: 550px;
        position: relative;
        img{
            position: absolute;
            height: 387px;
            left: 130px;
            top: 84px;
        }
    }
    .login-content-2{
        width: 350px;
        height: 550px;
        .login-form{
            display: flex;
            flex-direction: column;
            padding: 30px;
            height: 300px;
            background-color: $secondary;
            box-shadow: -1px 0px 0px 0px rgba(163,159,159,0.75);
            .title{
                display: flex;
                align-items: center;
                justify-content: center;
                font-size: 40px;
                font-family: 'Style Script', cursive;
                font-weight: bolder;
                margin-bottom: 10px;
            }
            input{
                height: 20px;
                margin: 10px 0;
                border-radius: 3px;
                border: 0.7px solid rgba(0, 0, 0, 0.172);
                background-color: rgba(231, 229, 229, 0.269);
                padding: 10px;
            }
            button{
                background-color: rgba($color: #0073ff, $alpha: 1.0);
                color: white;
                border: none;
                border-radius: 3px;
                margin-top: 10px;
                height: 30px;
                font-weight: 700;
                cursor: pointer;
            }
            .fgpass{
                text-decoration: none;
                color: #093872;
                text-align: center;
                margin-top: 10px;
                font-size: 14px;
            }
        }
        .register-button{
            box-shadow: -1px 0px 0px 0px rgba(163,159,159,0.75);
            background-color: white;
            display: flex;
            justify-content: center;
            align-items: center;
            margin-top: 10px;
            height: 60px;
            a{
                text-decoration: none;
                margin-left: 3px;
                color: #0073ff;
                font-weight: 500;
            }
        }
        .title-content-2{
            display: flex;
            justify-content: center;
            align-items: center;
            margin: 5px 0;
            height: 30px;

        }
        .install-button{
            height: 85px;
            display: flex;
            justify-content: center;
            gap: 10px;
            .install-image-1{
                background-image: url(https://www.instagram.com/static/images/appstore-install-badges/badge_ios_english-en.png/180ae7a0bcf7.png);
                background-size: 100%;
                background-repeat: no-repeat;
                height: 85px;
                width: 160px;
            }
            .install-image-2{
                background-image: url(https://www.instagram.com/static/images/appstore-install-badges/badge_android_english-en.png/e9cd846dc748.png);
                background-size: 100%;
                background-repeat: no-repeat;
                height: 85px;
                width: 160px;
            }
        }
    }

}
@media only screen and (max-width: 769px){
    .login-page{
        width: 100%;
        .login-content-1{
            background-image: none;
            position: absolute;
            display: none;
        }
        .install-button{
            width: 100%;
        }
    }
}

@media screen and (max-width: 415px){
    .login-page{
        display: flex;
        align-items: center;
        justify-content: center;
        background-color: white;
        position: absolute;
        width: 100vw;
        height: max-content;
        .login-content-2{
            width: 100%;
            .login-form{
                box-shadow: none;
                border: none;
                width: 100%;
                box-sizing: border-box;
                height: 60%;
            }
            .register-button{
                background-color: white;
                display: flex;
                justify-content: center;
                align-items: center;
                margin-top: 10px;
                width: 100%;
                height: 60px;
                box-shadow: none;
                a{
                    text-decoration: none;
                    margin-left: 3px;
                    color: #0073ff;
                    font-weight: 500;
                }
            }
            .install-button{
                height: 60px;
                width: 100%;
                display: flex;
                justify-content: center;
                align-items: center;
                .install-image-1{
                    height: 60px;
                    width: 130px;
                }
                .install-image-2{
                    height: 60px;
                    width: 130px;
                }
            }    
        }
        
        
    }
}
