@import url(https://fonts.googleapis.com/css2?family=Style+Script&display=swap);
.register-page{
    min-height: 100vh;
    background-color: rgba(231, 229, 229, 0.269);
    display: flex;
    justify-content: center;
    align-items: center;
    .register-content{
        width: 350px;
        height: 720px;
    
        .register-form{
            display: flex;
            flex-direction: column;
            padding: 30px;
            height: 470px;
            background-color: white;
            box-shadow: -1px 0px 0px 0px rgba(163,159,159,0.75);
            .title{
                display: flex;
                align-items: center;
                justify-content: center;
                font-size: 40px;
                font-family: 'Style Script', cursive;
                font-weight: bolder;
                margin-bottom: 10px;
            }
            .sub-title{
                font-weight: 700;
                opacity: 0.7;
                text-align: center;
                width: 200px;
                left: 50px;
                top: -10px;
                margin-bottom: 5px;
                position: relative;
            }
            input{
                height: 20px;
                margin: 10px 0;
                border-radius: 3px;
                border: 0.7px solid rgba(0, 0, 0, 0.172);
                background-color: rgba(231, 229, 229, 0.269);
                padding: 10px;
            }
            button{
                background-color: rgba($color: #0073ff, $alpha: 1.0);
                color: white;
                border: none;
                border-radius: 3px;
                margin-top: 10px;
                height: 30px;
                font-weight: 700;
                cursor: pointer;
            }
            .terms{
                margin-top: 10px;
                text-align: center;
                opacity: 0.7;
            }
        }
        .register-button{
            box-shadow: -1px 0px 0px 0px rgba(163,159,159,0.75);
            background-color: white;
            display: flex;
            justify-content: center;
            align-items: center;
            margin-top: 10px;
            height: 60px;
            a{
                text-decoration: none;
                margin-left: 3px;
                color: #0073ff;
                font-weight: 500;
            }
        }
        .title-content-2{
            display: flex;
            justify-content: center;
            align-items: center;
            margin: 5px 0;
            height: 30px;
    
        }
        .install-button{
            height: 85px;
            display: flex;
            justify-content: center;
            gap: 10px;
            .install-image-1{
                background-image: url(https://www.instagram.com/static/images/appstore-install-badges/badge_ios_english-en.png/180ae7a0bcf7.png);
                background-size: 100%;
                background-repeat: no-repeat;
                height: 85px;
                width: 160px;
            }
            .install-image-2{
                background-image: url(https://www.instagram.com/static/images/appstore-install-badges/badge_android_english-en.png/e9cd846dc748.png);
                background-size: 100%;
                background-repeat: no-repeat;
                height: 85px;
                width: 160px;
            }
        }
    }
}
@media only screen and (max-width: 415px){
    .register-page{
        background-color: white;
        width: 100%;
        .register-content{
            width: 100%;
            height: 100%;
            box-sizing: border-box;
            .register-form{
                height: 470px;
                .sub-title{
                    left: 0px;
                    width: 100%;
                }
            }
            .register-button{
                box-shadow: -1px 0px 0px 0px rgba(163,159,159,0.75);
                background-color: white;
                display: flex;
                justify-content: center;
                align-items: center;
                margin-top: 0px;
                height: 20px;
            }
            .install-button{
                height: 60px;
                width: 100%;
                display: flex;
                justify-content: center;
                align-items: center;
                .install-image-1{
                    height: 60px;
                    width: 130px;
                }
                .install-image-2{
                    height: 60px;
                    width: 130px;
                }
            } 
        }
    }
}
