    min-height: calc(100vh - (#{$footer + $navbar}));
    background-color: $primary;
    display: flex;
    justify-content: center;
    align-items: center;
    padding-top: 10px;
    box-sizing: border-box;
    font-family: 'Poppins', sans-serif;
    font-size: 14px;
    .register-content{
        width: 350px;
        height: 800px;
        .register-form{
            display: flex;
            flex-direction: column;
            padding: 30px;
            height: 550px;
            background-color: $secondary;
