            width: 100%;
            box-sizing: border-box;
            height: auto;
            display: flex;
            flex-direction: column;
            justify-content: center;
            align-items: center;
            .register-form{
                height: auto;
                border: none;
                box-shadow: none;
                width: 75%;
                .sub-title{
                    left: 0px;
                    width: 100%;
                }
                .google-btn{
                    .google-img{
                        height: 30px;
                    }
                }
            }
            .register-button{
                box-shadow: none;
                background-color: $secondary;
                display: flex;
                justify-content: center;
                align-items: center;
                margin-top: 0px;
                height: 20px;
                border: none;
            }
            .install-button{
                height: 60px;
                width: 100%;
                display: flex;
                justify-content: center;
                align-items: center;
                .install-image-1{
                    height: 60px;
                    width: 130px;
                }
                .install-image-2{
                    height: 60px;
                    width: 130px;
                }
            } 
