        height: 600px;
    
        .fgpass-form{
            display: flex;
            flex-direction: column;
            padding: 30px;
            height: 450px;
            background-color: $secondary;
            box-shadow: -1px 0px 0px 0px rgba(163,159,159,0.75);
            color: $text;
            .logo{
                margin-bottom: 10px;
