@import '../../globalData';

.page-container{
    background-color: $primary;
    display: flex;
    justify-content: center;
    align-items: center;
    min-height: calc(100vh - #{$navbar});

    .container{
        min-height: 800px;
        width: 600px;
        
        background-color: $secondary;
        border: 1px solid rgba(0, 0, 0, 0.172);
        border-radius: 5px;
        margin-bottom: 20px;
        
        .detail-container-1{

            .detail-container{
                height: 50px;
                border-bottom: 1px solid rgba(0, 0, 0, 0.172);
                display: flex;
                align-items: center;
                position: relative;
                .dc-1{
                    height: 100%;
                    width: 520px;
                    display: flex;
                    align-items: center;
                    gap: 10px;
                    box-sizing: border-box;
                    padding: 0 10px;
                    img{
                        object-fit: cover;
                        height: 35px;
                        border-radius: 50%;
                        width: 35px;
                        border: 2px solid;
                        border-color: rgb(230, 76, 15);
                        padding: 1px;
                        box-sizing: border-box;
                        &:hover{
                            + iframe{
                                display: block;
                            }
                        }
                    }
                    .dc-1-name{
                        a{
                            color: $text;
                            text-decoration: none;
                        }
                    }
                }
                iframe{
                    position: absolute;
                    top: 30px;
                    left: 30px;
                    border: 3px solid $line;
                    border-radius: 20px;
                    display: none;
                    &:hover{
                        display: block;
                    }
                }
                .dc-2{
                    height: 100%;
                    width: 80px;
                    display: flex;
                    justify-content: center;
                    button{
                        text-decoration: none;
                        font-size: 32px;
                        color: $text;
                        display: flex;
                        justify-content: flex-start;
                        align-items: flex-start;
                        background-color: $secondary;
                        border: none;
                        cursor: pointer;
                    }
                }
            }
            .detail-container-image{
                height: 600px;
                width: 600px;
                img{
                    height: 600px;
                    width: 600px;
                    object-fit: contain;
                }
                video{
                    height: 600px;
                    width: 600px;
                    object-fit: cover;
                }
                
            }
        }
        .detail-container-2{

            .detail-container-icon{
                padding: 5px 0;
        
                .icons{
                    height: 30px;
                    width: 30px;
                    color: black;
                }
                display: flex;
                justify-content: space-between;
                .button-icon-1{
                    padding-left: 5px;
                    position: relative;
                    
                    button{
                        margin: 0 5px;
                        background-color: $secondary;
                        border: none;
                        cursor: pointer;
                        .icons{
                            color: $text;
                        }
                        .plane{
                            transform: rotate(60deg);
                            position: absolute;
                            top: -3px;
                            left: 90px;
                        }
                    }
        
                    
                }
                .button-icon-2{
                    button{
                        margin: 0 5px;
                        background-color: $secondary;
                        border: none;
                        cursor: pointer;
                        .icons{
                            color: $text;
                        }
                    }
                }
            }
            .detail-like{
                padding: 0 10px;
                display: flex;
                gap: 5px;
                .people{
                    font-weight: 600;
                }
            }
            .detail-container-comment{
                margin-top: 5px;
                .detail-comment{
                    display: flex;
                    justify-content: space-between;
                    padding: 0 10px;
                    .container-comment{
                        display: flex;
                        gap: 5px;
                        .people{
                            font-weight: 600;
                        }
                    }
                    .detail-comment-button{
                        display: flex;
                        gap: 5px;
                        .comment-button{
                            border: none;
                            cursor: pointer;
                            background-color: $secondary;
                            .comment-icon{
                                height: 20px;
                                background-color: $secondary;
                                color: $text;
                                
                            }
                        }
                    }
                }
                .showmore-btn{
                    background-color: $secondary;
                    color: $line;
                    border: none;
                    cursor: pointer;
                    margin-left: 8px;
                }
            }
            .detail-container-add-comment{
                margin-top: 10px;
                border-top: 1px solid rgba(0, 0, 0, 0.172);
                padding: 0 10px;
                height: 30px;
                display: flex;
                #addComment{
                    height: 30px;
                    width: 540px;
                    border: none;
                    resize: none;
                    outline: none;
                    background-color: $secondary;
                    color: $text;
                    font-family: 'Poppins', sans-serif;
                }
                
                .post-btn{
                    height: 100%;
                    background-color: $secondary;
                    border: none;
                    cursor: pointer;
                    color: #0073ff;
        
                    font-weight: 700;
                }
            }
        }
    }
}


.popup-share{
    width: 200px;
    background-color: $primary;
    display: flex;
    position: relative;
    button{
        width: 100%;
        .popup-icon{
            height: 25px;
        }
        img{
            height: 25px;
        }

    }
}

@media screen and (max-width: 600px){
    .container{
        border-radius: 0;
        width: 100%;
        min-height: 100px;
        .detail-container-image{
            width: 100%;
            height: 100%;
            img{
                width: 100%;
                // height: 100%;
            }
        }
    }
            
}
