@import '../../globalData';
.cpas-page{
    min-height: calc(100vh - (#{$footer + $navbar}));
    background-color: $primary;
    display: flex;
    justify-content: center;
    align-items: center;
    font-family: 'Poppins', sans-serif;
    font-size: 14px;
    .cpas-content{
        width: 400px;
        height: 600px;
        display: flex;
        align-items: center;
        .cpas-form{
            width: 400px;
            display: flex;
            flex-direction: column;
            padding: 30px;
            height: 450px;
            background-color: $secondary;
            // box-shadow: -1px 0px 0px 0px rgba(163,159,159,0.75);
            border: 1px solid rgba(163,159,159,0.75);
            border-radius: 3px;
            color: $text;
            input{
                height: 20px;
                margin: 10px 0;
                border-radius: 3px;
                border: 0.7px solid rgba(0, 0, 0, 0.172);
                background-color: rgba(231, 229, 229, 0.269);
                padding: 10px;
                color: $text;
            }
            button{
                background-color: rgba($color: #0073ff, $alpha: 1.0);
                color: white;
                border: none;
                border-radius: 3px;
                margin-top: 10px;
                height: 30px;
                font-weight: 700;
                cursor: pointer;
            }
           
            .title{
                display: flex;
                align-items: center;
                justify-content: center;
                font-size: 20px;
                font-weight: bolder;
                margin-bottom: 10px;
                text-align: center;
            }
            
            .error{
                color: rgb(243, 92, 92);
                display: flex;
                justify-content: center;
                margin: -8px 0;
                font-weight: 600;
            }
        }
    }
}
@media only screen and (max-width: 415px){
    .cpas-page{
        width: 100%;
        .cpas-content{
            width: 85%;
            height: calc(100vh - (#{$navbar + $footer}));
            box-sizing: border-box;
            overflow: hidden;
            .cpas-form{
                border: none;
                height: 95%;
                box-sizing: border-box;
                
            }
        }
    }
}
