@import '../../globalData';
@import url(https://fonts.googleapis.com/css2?family=Style+Script&display=swap);

.login-page{
    display: flex;
    align-items: center;
    justify-content: center;
    min-height: calc(100vh - (#{$footer + $navbar}));
    color: $text !important;
    gap: 20px;
    font-family: 'Poppins', sans-serif;
    font-size: 14px;
    background-color: $primary;
    .login-content-1{
        background-image: url(https://www.instagram.com/static/images/homepage/home-phones@2x.png/9364675fb26a.png);
        background-size: 100%;
        background-repeat: no-repeat;
        width: 400px;
        height: 600px;
        position: relative;
        img{
            position: absolute;
            height: 387px;
            left: 130px;
            top: 84px;
        }
    }
    .login-content-2{
        width: 350px;
        height: 600px;
        .login-form{
            display: flex;
            flex-direction: column;
            padding: 30px;
            height: 350px;
            background-color: $secondary;
            border: 1px solid rgba(163,159,159,0.75);
            border-radius: 3px;
            // box-shadow: 0px 0px 0px 0px rgba(163,159,159,0.75);
            .title{
                display: flex;
                align-items: center;
                justify-content: center;
                font-size: 40px;
                font-family: 'Style Script', cursive;
                font-weight: bolder;
                margin-bottom: 10px;
                color: $text;
            }
            input{
                height: 20px;
                margin: 10px 0;
                border-radius: 3px;
                border: 0.7px solid rgba(0, 0, 0, 0.172);
                background-color: rgba(231, 229, 229, 0.269);
                padding: 10px;
                color: $text;
            }
            .error-message{
                color: rgb(243, 92, 92);
                display: flex;
                justify-content: center;
                margin: -8px 0;
                font-weight: 600;
            }
            .verif-btn{

                text-decoration: none;
                .verify{
                    color: $button;
                    text-align: center;
                    margin-top: 10px;
                    font-size: 14px;
                }
            }
            button{
                background-color: rgba($color: #0073ff, $alpha: 1.0);
                color: white;
                border: none;
                border-radius: 3px;
                margin-top: 10px;
                min-height: 30px;
                height: 100%;
                font-weight: 700;
                cursor: pointer;
                .loading-moment{
                    display: flex;
                    flex-direction: column;
                    align-items: center;
                    padding: 5px 0;
                    margin-bottom: 5px;
                    .loading{
                        border: 5px solid rgb(131, 131, 131);
                        border-radius: 50%;
                        border-top: 5px solid white;
                        width: 10px;
                        height: 10px;
                        animation: spin 800ms linear infinite;
                        position: relative;
                    }
                }
            }
            .fgpass{
                text-decoration: none;
                color: $button;
                text-align: center;
                margin-top: 10px;
                font-size: 14px;
            }
            .or{
                &:before{
                    content: '';
                    width: 43%;
                    border-bottom: 1px solid $text;
                    position: absolute;
                    left: 0;
                    top: 50%;
                    opacity: 0.7;
                }
                position: relative;
                margin: 10px 0;
                text-align: center;
                color: $text;
                font-weight: 800;
                font-size: small;
                opacity: 0.7;
                &:after{
                    opacity: 0.7;
                    content: '';
                    width: 43%;
                    border-bottom: 1px solid $text;
                    position: absolute;
                    right: 0;
                    top: 50%;
                }
            }
            .google-btn{
                color: white;
                display: flex;
                position: relative;
                .google-img{
                    background-position: center;
                    background-image: url("https://upload.wikimedia.org/wikipedia/commons/5/53/Google_%22G%22_Logo.svg");
                    background-color: white;
                    background-repeat: no-repeat;
                    border: 1px solid #0073ff;
                    width: 50px;
                    height: 100%;
                    position: relative;
                    box-sizing: border-box;
                    border-top-left-radius: 2px;
                    border-bottom-left-radius: 2px;
                }
                .google-text{
                    color: white;
                    position: absolute;
                    width: 100%;
                    height: 100%;
                    display: flex;
                    align-items: center;
                    justify-content: center;
                    margin-left: 15px;
                }
            }
        }
        .register-button{
            // box-shadow: -1px 0px 0px 0px rgba(163,159,159,0.75);
            border: 1px solid rgba(163,159,159,0.75);
            border-radius: 3px;
            background-color: $secondary;
            display: flex;
            justify-content: center;
            align-items: center;
            margin-top: 10px;
            height: 60px;
            color: $text;
            a{
                text-decoration: none;
                margin-left: 3px;
                color: #0073ff;
                font-weight: 500;
            }
        }
        .title-content-2{
            display: flex;
            justify-content: center;
            align-items: center;
            margin: 5px 0;
            height: 30px;

        }
        .install-button{
            height: 85px;
            display: flex;
            justify-content: center;
            gap: 10px;
            .install-image-1{
                background-image: url(https://www.instagram.com/static/images/appstore-install-badges/badge_ios_english-en.png/180ae7a0bcf7.png);
                background-size: 100%;
                background-repeat: no-repeat;
                height: 85px;
                width: 160px;
            }
            .install-image-2{
                background-image: url(https://www.instagram.com/static/images/appstore-install-badges/badge_android_english-en.png/e9cd846dc748.png);
                background-size: 100%;
                background-repeat: no-repeat;
                height: 85px;
                width: 160px;
            }
        }
    }

}
@media only screen and (max-width: 769px){
    .login-page{
        width: 100%;
        .login-content-1{
            background-image: none;
            position: absolute;
            display: none;
        }
        .install-button{
            width: 100%;
        }
    }
}

@media screen and (max-width: 415px){
    .login-page{
        display: flex;
        align-items: center;
        justify-content: center;
        background-color: $secondary;
        height: calc(100vh - (#{$navbar}));
        
        .login-content-2{
            width: 100%;
            height: auto;
            display: flex;
            flex-direction: column;
            align-items: center;
            .login-form{
                box-shadow: none;
                border: none;
                width: 90%;
                box-sizing: border-box;
                height: 60%;
                padding-bottom: 10px;
                .google-btn{
                    .google-img{
                        height: 30px;
                    }
                }
            }
            .register-button{
                background-color: $secondary;
                display: flex;
                justify-content: center;
                align-items: center;
                margin-top: 10px;
                width: 90%;
                height: 60px;
                box-shadow: none;
                border: none;
                a{
                    text-decoration: none;
                    margin-left: 3px;
                    color: #0073ff;
                    font-weight: 500;
                }
            }
            .install-button{
                height: 60px;
                width: 90%;
                display: flex;
                justify-content: center;
                align-items: center;
                .install-image-1{
                    height: 60px;
                    width: 130px;
                }
                .install-image-2{
                    height: 60px;
                    width: 130px;
                }
            }    
        }
        
        
    }
}

