.fgpass-page{
    min-height: 100vh;
    background-color: rgba(231, 229, 229, 0.269);
    display: flex;
    justify-content: center;
    align-items: center;
    .fgpass-content{
        width: 400px;
        height: 600px;
    
        .fgpass-form{
            display: flex;
            flex-direction: column;
            padding: 30px;
            height: 450px;
            background-color: white;
            box-shadow: -1px 0px 0px 0px rgba(163,159,159,0.75);
            .logo{
                margin-bottom: 10px;
                display: flex;
                justify-content: center;
                align-items: center;
                .lock{
                    height: 100px;
                }
            }
            .title{
                display: flex;
                align-items: center;
                justify-content: center;
                font-size: 20px;
                font-weight: bolder;
                margin-bottom: 10px;
            }
            .sub-title{
                font-weight: 700;
                opacity: 0.7;
                text-align: center;
                margin-bottom: 5px;
                position: relative;
            }
            input{
                height: 20px;
                margin: 10px 0;
                border-radius: 3px;
                border: 0.7px solid rgba(0, 0, 0, 0.172);
                background-color: rgba(231, 229, 229, 0.269);
                padding: 10px;
            }
            button{
                background-color: rgba($color: #0073ff, $alpha: 1.0);
                color: white;
                border: none;
                border-radius: 3px;
                margin-top: 10px;
                height: 30px;
                font-weight: 700;
                cursor: pointer;
            }
            .or{
                &:before{
                    content: '';
                    width: 43%;
                    border-bottom: 1px solid rgba(0, 0, 0, 0.296);
                    position: absolute;
                    left: 0;
                    top: 50%;
                }
                position: relative;
                margin: 10px 0;
                text-align: center;
                &:after{
                    content: '';
                    width: 43%;
                    border-bottom: 1px solid rgba(0, 0, 0, 0.296);
                    position: absolute;
                    right: 0;
                    top: 50%;
                }
            }
            .register-button{
                display: flex;
                justify-content: center;
                align-items: center;
                a{
                    color: black;
                    font-weight: 700;
                    text-decoration: none;

                }
            }
        }
        .login-button{
            box-shadow: -1px 0px 0px 0px rgba(163,159,159,0.75);
            background-color: white;
            display: flex;
            justify-content: center;
            align-items: center;
            height: 60px;
            a{
                button{
                    font-weight: 600;
                    height: 60px;
                    width: 400px;
                    cursor: pointer;
                }
            }
        }
        .title-content-2{
            display: flex;
            justify-content: center;
            align-items: center;
            margin: 5px 0;
            height: 30px;
    
        }
    }
}
@media only screen and (max-width: 415px){
    .fgpass-page{
        background-color: white;
        width: 100%;
        .fgpass-content{
            width: 100%;
            height: 100vh;
            box-sizing: border-box;
            overflow: hidden;
            .fgpass-form{
                height: 95%;
                box-sizing: border-box;
                .sub-title{
                    left: 0px;
                    width: 100%;
                }
            }
            .login-button{
                box-shadow: -1px 0px 0px 0px rgba(163,159,159,0.75);
                background-color: white;
                display: flex;
                justify-content: center;
                align-items: center;
                margin-top: 0px;
                height: 20px;
                a{
                    button{
                        width: 100vw;
                    }
                }
            }
        }
    }
}
