@import '../../globalData';
.setting-page{
    background-color: $primary;
    min-height: calc(100vh - (#{$navbar + $footer}));
    display: flex;
    justify-content: center;
    align-items: center;
    font-family: 'Poppins', sans-serif;
    .main-setting{
        display: flex;
        background-color: $secondary;
        width: 800px;
        min-height: 600px;
        color: $text;
        .main-1{
            display: flex;
            flex-direction: column;
            width: 250px;
            position: relative;
            border: 0.2px solid rgba(0, 0, 0, 0.172);
            
            .main-1-container-1{
                
                border-bottom: none;
                border-right: none;
                display: flex;
                flex-direction: column;
                min-height: 500px;
                border-top-left-radius: 3px;
                button{
                    background-color: $secondary;
                    border: none;
                    height: 50px;
                    cursor: pointer;
                    color: $text;
                    &:hover{
                        background-color: $pnbtn;
                        
                    }
                }
            }
            .main-1-container-2{
                height: 100px;
                display: flex;
                justify-content: center;
                align-items: center;
                border-top: 0.2px solid rgba(0, 0, 0, 0.172);
                border-bottom-left-radius: 3px;
                position: absolute;
                bottom: 0px;
                width: 100%;
                .title{
                    font-family: 'Style Script', cursive;
                    font-weight: 900;
                    font-size: 28px;
                }
            }
        }
        .main-2{
            padding: 30px;
            border: 0.2px solid rgba(0, 0, 0, 0.172);
            border-left: none;
            border-top-right-radius: 3px;
            border-bottom-right-radius: 3px;
            width: 550px;
            tr{
                height: 60px;
                .left{
                    text-align: right;
                    vertical-align: top;
                    font-weight: 600;
                    img{
                        height: 50px;
                        width: 50px;
                        border-radius: 50%;
                        object-fit: cover;
                    }
                }
                .right{
                    padding-left: 30px;
                    padding-bottom: 20px;
                    vertical-align: top;
                    button{
                        width: 150px;
                        height: 25px;
                        background-color: #0073ff;
                        border: none;
                        border-radius: 3px;
                        color: white;
                        font-weight: 600;
                        cursor: pointer;
                    }
                    
                    .input-desc{
                        font-size: 12px;
                        opacity: 0.8;
                        width: 250px;
                    }
                    input{
                        font-family: 'Poppins', sans-serif;
                        width: 250px;
                    }
                    textarea{
                        font-family: 'Poppins', sans-serif;
                        columns: 250px;
                        min-height: 50px;
                        width: 250px;
                        resize: vertical;
                    }
                    .change-pp-btn{
                        display: flex;
                        justify-content: center;
                        .upload-picture{
                            display: none;
                        }
                    }
                    
                }
                .for-btn{
                    display: flex;
                }
            }
        }
    }
}

@media screen and (max-width: 800px) {
    .setting-page{
        .main-setting{
            width: 100%;
        }
    }
}
