                                img{
                                    width: 40px;
                                    border-radius: 50%;
                                    object-fit: cover;
                                    margin-left: 10px;
                                }
                            }
                        }
                    }
                }
                .inputan-message{
                    position: absolute;
                    bottom: 10px;
                    width: 100%;
                    padding: 10px;
                    box-sizing: border-box;
                    gap: 5px;
                    display: flex;
                    .input-text{
                        width: 80%;
                        height: 30px;
                        border-radius: 5px;
                        background-color: $primary;
                        color: $text;
                    }
                    .submit-button{
                        width: 15%;
                        background-color: rgba($color: #0073ff, $alpha: 1.0);
                        color: white;
                        border: none;
                        border-radius: 3px;
                        font-weight: 700;
                        cursor: pointer;
                    }
                }
            }
        }
    }
}

