@import '../../globalData';
.profile-page{
    background-color: $primary;
    min-height: calc(100vh - 55px - 80px);
    height: 100%;
    display: flex;
    align-items: center;
    box-sizing: border-box;
    padding: 30px;
    flex-direction: column;
    font-family: 'Poppins', sans-serif;
    .profile-content-1{
        display: flex;
        width: 700px;
        .profile-picture{
            height: 200px;
            width: 200px;
            display: flex;
            justify-content: center;
            align-items: center;
            img{
                object-fit: cover;
                width: 150px;
                height: 150px;
                border-radius: 50%;
            }
        }
        .profile-detail{
            box-sizing: border-box;
            padding: 10px;
            width: calc(100% - 200px);
            .detail-container-1{
                display: flex;
                .detail-name{
                    font-size: 25px;
                }
                .detail-button{
                    button{
                        margin: 5px 10px;
                        width: 90px;
                        height: 25px;
                        background-color: #0073ff;
                        border: none;
                        border-radius: 3px;
                        color: white;
                        font-weight: 600;
                        cursor: pointer;
                    }
                }
            }
        }
        .detail-fp{
            display: flex;
            gap: 30px;
            margin: 10px 0;
            b{
                margin-right: 5px;
            }
        }
        .description{
            margin-top: 10px;
            font-size: 15px;
            word-wrap: break-word;
        }
    }
    .profile-content-2{
        display: flex;
        flex-direction: column;
        flex-wrap: wrap;
        width: 700px;
        align-items: center;
        .description-2{
            display: none;
        }
        .detail-fp-2{
            display: none;
        }
        .button-content-2{
            border-top: 1px solid rgba(0, 0, 0, 0.11);
            width: 80%;
            display: flex;
            justify-content: center;
            gap: 30px;
            margin: 20px 0;
            button{
                width: 100px;
                border: none;
                border-top: 0.1px solid rgba(0, 0, 0, 0.11);
                height: 30px;
                background-color: rgb(249 248 248);
                cursor: pointer;
                &.current{
                    font-weight: 700;
                    border-top: 2px solid black;
                }
            }
        }
        .image-content-2{
            display: grid;
            grid-template-columns: 250px 200px 250px;
            grid-auto-rows: 220px;
            justify-items: center;
            a{
                
                display: flex;
                justify-content: center;
                align-items: center;
                width: 200px;
                height: 200px;
                img{
                    object-fit: cover;
                    width: 200px;
                    height: 200px;
                }
            }
        }
    }
}

@media screen and (max-width: 720px){
    .profile-page{
        height: 100%;
        padding: 20px 0 0 0;
        .profile-content-1{
            width: 100%;
            display: flex;
            padding: 0 0 20px 20px;
            box-sizing: border-box;
            .profile-picture{
                width: 100px;
                height: 100px;
                img{
                    width: 100px;
                    height: 100px;
                }
            }
            .profile-detail{
                width: 60%;
                .detail-container-1{
                    width: 100%;
                    flex-direction: column;
                    .detail-name{
                        text-overflow: ellipsis;
                        overflow: hidden;
                        width: 100%;
                        
                    }
                    .detail-button{
                        button{
                            margin: 0 0;
                            width: 100%;
                        }
                    }
                }

                .detail-fp{
                    display: none;
                }
                .description{
                    display: none;
                }
            }
        }
        .profile-content-2{
            width: 100%;
            box-sizing: border-box;
            .description-2{
                width: 100%;
                word-wrap: break-word;
                display: block;
                box-sizing: border-box;
                padding: 0 20px 20px 20px;
            }
            .detail-fp-2{
                width: 100%;
                display: flex;
                height: 50px;
                justify-content: center;
                align-items: center;
                border-top: 1px solid rgba(0, 0, 0, 0.2);
                padding-top: 10px;
                .posts{
                    width: calc(100% / 3);
                    display: flex;
                    flex-direction: column;
                    justify-content: center;
                    align-items: center;
                }
                .followers{
                    width: calc(100% / 3);
                    display: flex;
                    flex-direction: column;
                    justify-content: center;
                    align-items: center;

                }
                .following{
                    width: calc(100% / 3);
                    display: flex;
                    flex-direction: column;
                    justify-content: center;
                    align-items: center;
                }
            }
            .button-content-2{
                width: 100%;
            }
            .image-content-2{
                width: 100%;
                overflow: hidden;
                grid-template-columns: repeat(3, calc(100vw / 3));
                grid-auto-rows: calc(100vw / 3);
                grid-gap: 3px;
                a{
                    width: 100%;
                    height: 100%;
                    img{
                        width: 100%;
                        height: 100%;
                    }
                }
            }
        }
    }
}
